    this.id = id
    this.state = state
    this.name = name
    this.name_tmp = name_tmp
    this.size = size
    this.ext = ext
    this.type = type
    this.type_file = type_file
    this.author = {
      id: author_id,
      name: author_name,
    }
    this.description = description
    this.divIndex = divIndex
    this.name_tmp_complete = URL_S3_IMAGE + 'files/images/' + name_tmp
