const modalChangePasswordUser = $('#modal-change-of-password')
const formChangePasswordUser = $('#form-change-password-user')
const messageErrorUserPass = modalChangePasswordUser.find(
  '.error-change-password',
)

$('.button-modal-change-of-password').click(function (e) {
  modalChangePasswordUser.modal('show')
  messageErrorUserPass.hide()
  messageErrorUserPass.text('')
  UtilFormClose(formChangePasswordUser)
  modalChangePasswordUser.find('.overlay').hide()
})

modalChangePasswordUser.find('.button-send-password').click(function () {
  messageErrorUserPass.text('')
  messageErrorUserPass.hide()
  UtilModalLoader(modalChangePasswordUser)
  SendFormUpdateChangePasswordUser.Send()
})

let SendFormUpdateChangePasswordUser = new QueryAjax({
  form: 'form-change-password-user',
  action: 'ActionUpdateChangePasswordUser',
})

function ActionUpdateChangePasswordUser(status, data) {
  if (!status) {
    console.log(status)
  } else if (data.status) {
    modalChangePasswordUser.modal('hide')
    notify(
      false,
      'Usuario Actualizado',
      'La contraseña se actualizo correctamente.',
      2,
    )
    UtilFormClose(formChangePasswordUser)
  } else {
    messageErrorUserPass.html(data.message)
    messageErrorUserPass.show()
  }
  UtilModalLoader(modalChangePasswordUser, false)
}

/**
 * Tool for creating image Blocks for Editor.js
 * Made with «Creating a Block Tool» tutorial {@link https://editorjs.io/creating-a-block-tool}
 *
 * @typedef {object} ImageToolData — Input/Output data format for our Tool
 * @property {string} url - image source URL
 * @property {int} galleryType -  gallery type
 * @property {string} caption - image caption
 * @property {boolean} withBorder - flag for adding a border
 * @property {boolean} withBackground - flag for adding a background
 * @property {boolean} stretched - flag for stretching image to the full width of content
 *
 * @typedef {object} ImageToolConfig
 * @property {string} placeholder — custom placeholder for URL field
 */
class EditorJsSimpleImage {
  /**
   * Our tool should be placed at the Toolbox, so describe an icon and title
   */
  static get toolbox() {
    return {
      title: 'Imagenes',
      icon:
        '<svg width="17" height="15" viewBox="0 0 336 276" xmlns="http://www.w3.org/2000/svg"><path d="M291 150V79c0-19-15-34-34-34H79c-19 0-34 15-34 34v42l67-44 81 72 56-29 42 30zm0 52l-43-30-56 30-81-67-66 39v23c0 19 15 34 34 34h178c17 0 31-13 34-29zM79 0h178c44 0 79 35 79 79v118c0 44-35 79-79 79H79c-44 0-79-35-79-79V79C0 35 35 0 79 0z"/></svg>',
    }
  }

  /**
   * Allow render Image Blocks by pasting HTML tags, files and URLs
   * @see {@link https://editorjs.io/paste-substitutions}
   * @return {{tags: string[], files: {mimeTypes: string[], extensions: string[]}, patterns: {image: RegExp}}}
   */
  static get pasteConfig() {
    return {
      tags: ['IMG'],
      files: {
        mimeTypes: ['image/*'],
        extensions: ['gif', 'jpg', 'png'], // You can specify extensions instead of mime-types
      },
      patterns: {
        image: /https?:\/\/\S+\.(gif|jpe?g|tiff|png)$/i,
      },
    }
  }

  /**
   * Automatic sanitize config
   * @see {@link https://editorjs.io/sanitize-saved-data}
   */
  static get sanitize() {
    return {
      url: {},
      caption: {
        b: true,
        a: {
          href: true,
        },
        i: true,
      },
    }
  }

  /**
   * Tool class constructor
   * @param {ImageToolData} data — previously saved data
   * @param {object} api — Editor.js Core API {@link  https://editorjs.io/api}
   * @param {ImageToolConfig} config — custom config that we provide to our tool's user
   */
  constructor({ data, api, config }) {
    this.api = api
    this.config = config || {}
    this.data = {
      images: data.images || [],
      galleryType: data.galleryType || 1,
      withBorder: data.withBorder !== undefined ? data.withBorder : false,
      withBackground:
        data.withBackground !== undefined ? data.withBackground : false,
      stretched: data.stretched !== undefined ? data.stretched : false,
    }

    this.blockNumber = null

    this.wrapper = undefined
    this.settings = [
      {
        name: 'withBorder',
        icon: `<svg width="20" height="20" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path d="M15.8 10.592v2.043h2.35v2.138H15.8v2.232h-2.25v-2.232h-2.4v-2.138h2.4v-2.28h2.25v.237h1.15-1.15zM1.9 8.455v-3.42c0-1.154.985-2.09 2.2-2.09h4.2v2.137H4.15v3.373H1.9zm0 2.137h2.25v3.325H8.3v2.138H4.1c-1.215 0-2.2-.936-2.2-2.09v-3.373zm15.05-2.137H14.7V5.082h-4.15V2.945h4.2c1.215 0 2.2.936 2.2 2.09v3.42z"/></svg>`,
      },
      {
        name: 'stretched',
        icon: `<svg width="17" height="10" viewBox="0 0 17 10" xmlns="http://www.w3.org/2000/svg"><path d="M13.568 5.925H4.056l1.703 1.703a1.125 1.125 0 0 1-1.59 1.591L.962 6.014A1.069 1.069 0 0 1 .588 4.26L4.38.469a1.069 1.069 0 0 1 1.512 1.511L4.084 3.787h9.606l-1.85-1.85a1.069 1.069 0 1 1 1.512-1.51l3.792 3.791a1.069 1.069 0 0 1-.475 1.788L13.514 9.16a1.125 1.125 0 0 1-1.59-1.591l1.644-1.644z"/></svg>`,
      },
      {
        name: 'withBackground',
        icon: `<svg width="20" height="20" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path d="M10.043 8.265l3.183-3.183h-2.924L4.75 10.636v2.923l4.15-4.15v2.351l-2.158 2.159H8.9v2.137H4.7c-1.215 0-2.2-.936-2.2-2.09v-8.93c0-1.154.985-2.09 2.2-2.09h10.663l.033-.033.034.034c1.178.04 2.12.96 2.12 2.089v3.23H15.3V5.359l-2.906 2.906h-2.35zM7.951 5.082H4.75v3.201l3.201-3.2zm5.099 7.078v3.04h4.15v-3.04h-4.15zm-1.1-2.137h6.35c.635 0 1.15.489 1.15 1.092v5.13c0 .603-.515 1.092-1.15 1.092h-6.35c-.635 0-1.15-.489-1.15-1.092v-5.13c0-.603.515-1.092 1.15-1.092z"/></svg>`,
      },
    ]
  }

  /**
   * Return a Tool's UI
   * @return {HTMLElement}
   */
  render() {
    this.wrapper = document.createElement('div')
    this.wrapper.classList.add('simple-image')

    this._addModalFile()
    this._addButtonModal()
    
    this.selectGalleryType = this.ModalFile.find('select[name=image_type]')
    this._addLabelGalleryType()

    if (this.data && this.data.images) {
      this.dataS3 = this.data.images
      this.galleryType = this.data.galleryType
      this._createImages(this.dataS3)
    }

    this.editorFileS3 = new updloadS3(this.ModalFile.find('.div-files-class'), {
      url: '/admin/files/items',
      typeFile: 1,
      id: 1,
      reload: false,
      limitFiles: 10,
    })

    var cont = this
    this.editorFileS3.send.click(function () {
      cont.dataS3 = cont._setDataImages(cont.editorFileS3.data)
      cont.galleryType = cont.selectGalleryType.val()
      cont._createImages(cont.dataS3)
      cont._openModal(false)
    })

    this.selectGalleryType.change(function () {
      cont._activeButtonS3()
    })

    if (!this.data.images.length) {
      this._openModal()
    }

    this._acceptTuneView(true)

    setTimeout(() => {
      if (this.blockNumber) {
        this.api.blocks.stretchBlock(this.blockNumber, true)
      }
    }, 100)

    return this.wrapper
  }

  /**
   * @private
   * Add modal with template file
   */
  _addModalFile() {
    let modalNameClass = 'modal_file_' + Math.floor(Math.random() * 1000 + 1)
    let modalTemplate = $($('#template-file-modal').html()).clone()
    modalTemplate.attr('class', 'modal fade modal-table-gear ' + modalNameClass)
    $('.modals-files').append(modalTemplate)
    this.ModalFile = $('.' + modalNameClass)
  }

  /**
   * @private
   * Open modal file
   */
  _openModal(status = true) {
    if (status) {
      this.ModalFile.modal('show')
      this.ModalFile.find('.overlay').hide()
    } else {
      this.ModalFile.modal('hide')
      this.ModalFile.find('.overlay').hide()
    }
  }

  /**
   * @private
   * active button of send s3
   */
  _activeButtonS3() {
    this.editorFileS3.send.prop('disabled', false)
  }

  /**
   * @private
   * Add button open modal
   */
  _addButtonModal() {
    const button = document.createElement('button')
    button.type = 'button'
    button.innerHTML = '<i class="fa fa-image"></i> Editar'

    button.value = this.config.placeholder || 'Paste an image URL...'
    button.addEventListener('click', (event) => {
      this.editorFileS3.clear()
      if (this.dataS3) {
        this.editorFileS3.loadData(this.dataS3, 1)
      }

      this.ModalFile.find('select[name=image_type]').val(this.galleryType)

      this.editorFileS3.loading.hide()
      this._openModal()
    })

    this.wrapper.appendChild(button)
  }

  /**
   * @private
   * Add label of gallery type
   */
  _addLabelGalleryType() {
    const label = document.createElement('label')
    label.className = 'label-type'
    label.innerHTML = this.galleryType == 1 ? 'Fotografías' : 'Galería'
    this.wrapper.appendChild(label)
  }

  /**
   * @private
   * Create image with caption field
   * @param {array} images — image source
   * @param {object} datacomplete
   */
  _createImages(images) {
    this.wrapper.innerHTML = ''
    this._addButtonModal()
    this._addLabelGalleryType()

    const galery = images.length > 6 ? 'general' : images.length
    const divImages = document.createElement('div')
    divImages.className = 'gallery gallery-' + galery
    var i = 1

    images.forEach((image) => {
      const divImage = document.createElement('figure')
      divImage.className = 'gallery__item  gallery__item--' + i

      const imageElement = document.createElement('img')
      imageElement.src = image.name_tmp_complete
      imageElement.className = 'gallery__img'
      divImage.appendChild(imageElement)

      divImages.appendChild(divImage)
      i++
    })
    this.wrapper.appendChild(divImages)
  }

  /**
   * Extract data from the UI
   * @param {HTMLElement} blockContent — element returned by render method
   * @return {SimpleImageData}
   */
  save(blockContent) {
    return Object.assign(this.data, {
      images: this.dataS3,
      galleryType: this.galleryType,
    })
  }

  /**
   * Set data images by save
   * @param {array} dataS3
   * @returns {array}
   */
  _setDataImages(dataS3) {
    if (!dataS3 && !data.isArray()) {
      return false
    }

    dataS3 = this._sortImages(dataS3)

    let data = []
    dataS3.forEach((element) => {
      data.push({
        author_id: element.author.id,
        author_name: element.author.name,
        description: element.description,
        ext: element.ext,
        id: element.id + Math.floor(Math.random() * 10000 + 1),
        name: element.name,
        name_tmp: element.name_tmp,
        name_tmp_complete: element.name_tmp_complete,
        order: element.order,
      })
    })

    return data
  }

  /**
   * sort images
   * @param {array} images
   * @returns
   */
  _sortImages(images) {
    return images.sort(function (a, b) {
      if (a.order > b.order) {
        return 1
      }
      if (a.order < b.order) {
        return -1
      }
      return 0
    })
  }

  /**
   * Skip empty blocks
   * @see {@link https://editorjs.io/saved-data-validation}
   * @param {ImageToolConfig} savedData
   * @return {boolean}
   */
  validate(savedData) {
    if (savedData.images.length == 0) {
      return false
    }
    return true
  }

  /**
   * Making a Block settings: 'add border', 'add background', 'stretch to full width'
   * @see https://editorjs.io/making-a-block-settings — tutorial
   * @see https://editorjs.io/tools-api#rendersettings - API method description
   * @return {HTMLDivElement}
   */
  renderSettings() {
    const wrapper = document.createElement('div')

    this.settings.forEach((tune) => {
      let button = document.createElement('div')

      button.classList.add(this.api.styles.settingsButton)
      button.classList.toggle(
        this.api.styles.settingsButtonActive,
        this.data[tune.name],
      )
      button.innerHTML = tune.icon
      wrapper.appendChild(button)

      button.addEventListener('click', () => {
        this._toggleTune(tune.name)
        button.classList.toggle(this.api.styles.settingsButtonActive)
      })
    })

    return wrapper
  }

  /**
   * @private
   * Click on the Settings Button
   * @param {string} tune — tune name from this.settings
   */
  _toggleTune(tune) {
    this.data[tune] = !this.data[tune]
    this._acceptTuneView()
  }

  /**
   * Add specified class corresponds with activated tunes
   * @private
   */
  _acceptTuneView(render = false) {
    this.settings.forEach((tune) => {
      this.wrapper.classList.toggle(tune.name, !!this.data[tune.name])

      if (tune.name === 'stretched') {
        if (this.data.stretched) {
          this.blockNumber = this.api.blocks.getCurrentBlockIndex() + 1
        }
        if (!render) {
          this.api.blocks.stretchBlock(
            this.api.blocks.getCurrentBlockIndex(),
            !!this.data.stretched,
          )
        }
      }
    })
  }
}

class EditorJsSummaryTop {
  /**
   * Notify core that read-only mode is supported
   *
   * @returns {boolean}
   */
  static get isReadOnlySupported() {
    return true
  }

  /**
   * Should this tool be displayed at the Editor's Toolbox
   *
   * @returns {boolean}
   * @public
   */
  static get displayInToolbox() {
    return true
  }

  /**
   * Allow to press Enter inside the RawTool textarea
   *
   * @returns {boolean}
   * @public
   */
  static get enableLineBreaks() {
    return true
  }

  /**
   * Get Tool toolbox settings
   * icon - Tool icon's SVG
   * title - title to show in toolbox
   *
   * @returns {{icon: string, title: string}}
   */
  static get toolbox() {
    return {
      icon:
        '<svg width="18px" height="18px" viewBox="0 0 18 18" id="icon" xmlns="http://www.w3.org/2000/svg">  <defs>    <style>      .cls-1 {        fill: none;      }    </style>  </defs>  <rect x="19" y="10" width="7" height="2"/>  <rect x="19" y="15" width="7" height="2"/>  <rect x="19" y="20" width="7" height="2"/>  <rect x="6" y="10" width="7" height="2"/>  <rect x="6" y="15" width="7" height="2"/>  <rect x="6" y="20" width="7" height="2"/>  <path d="M28,5H4A2.002,2.002,0,0,0,2,7V25a2.002,2.002,0,0,0,2,2H28a2.002,2.002,0,0,0,2-2V7A2.002,2.002,0,0,0,28,5ZM4,7H15V25H4ZM17,25V7H28V25Z"/>  <rect id="_Transparent_Rectangle_" data-name="&lt;Transparent Rectangle&gt;" class="cls-1" width="32" height="32"/></svg>',
      title: 'Resumen',
    }
  }

  /**
   * Render plugin`s main Element and fill it with saved data
   *
   * @param {RawData} data — previously saved HTML data
   * @param {object} config - user config for Tool
   * @param {object} api - CodeX Editor API
   * @param {boolean} readOnly - read-only mode flag
   */
  constructor({ data, config, api, readOnly }) {
    this.api = api
    this.readOnly = readOnly

    this.placeholder = config.placeholder || RawTool.DEFAULT_PLACEHOLDER

    this.CSS = {
      baseClass: this.api.styles.block,
      input: this.api.styles.input,
      wrapper: 'ce-rawtool',
      textarea: 'ce-rawtool__textarea',
    }

    this.data = {
      html: data.html || '',
    }

    this.textarea = null
    this.resizeDebounce = null
  }

  /**
   * Return Tool's view
   *
   * @returns {HTMLDivElement} this.element - RawTool's wrapper
   * @public
   */
  render() {
    const wrapper = document.createElement('div')
    const renderingTime = 100

    this.textarea = document.createElement('textarea')

    wrapper.classList.add(this.CSS.baseClass, this.CSS.wrapper)

    this.textarea.classList.add(this.CSS.textarea, this.CSS.input)
    this.textarea.textContent = this.data.html
    this.textarea.placeholder = this.placeholder

    if (this.readOnly) {
      this.textarea.disabled = true
    } else {
      this.textarea.addEventListener('input', () => {
        this.onInput()
      })
    }

    wrapper.appendChild(this.textarea)

    setTimeout(() => {
      this.resize()
    }, renderingTime)

    return wrapper
  }

  /**
   * Extract Tool's data from the view
   *
   * @param {HTMLDivElement} rawToolsWrapper - RawTool's wrapper, containing textarea with raw HTML code
   * @returns {RawData} - raw HTML code
   * @public
   */
  save(rawToolsWrapper) {
    return {
      html: rawToolsWrapper.querySelector('textarea').value,
    }
  }

  /**
   * Default placeholder for RawTool's textarea
   *
   * @public
   * @returns {string}
   */
  static get DEFAULT_PLACEHOLDER() {
    return 'Enter HTML code'
  }

  /**
   * Automatic sanitize config
   */
  static get sanitize() {
    return {
      html: true, // Allow HTML tags
    }
  }

  /**
   * Textarea change event
   *
   * @returns {void}
   */
  onInput() {
    if (this.resizeDebounce) {
      clearTimeout(this.resizeDebounce)
    }

    this.resizeDebounce = setTimeout(() => {
      this.resize()
    }, 200)
  }

  /**
   * Resize textarea to fit whole height
   *
   * @returns {void}
   */
  resize() {
    this.textarea.style.height = 'auto'
    this.textarea.style.height = this.textarea.scrollHeight + 'px'
  }
}
